var c=document.getElementById("matrix");var ctx=c.getContext("2d");var alphabet="゠アイウエオカキクケコサシスセソタチツテトナニヌネノハヒフヘホマミムメモヤユヨラリルレワヰヱヲンヺ・ーヽヿ0123456789";var characters=alphabet.split("");var fontSize,columns,drops,lastTime=0;init();function init(){fontSize=12*devicePixelRatio;c.height=window.innerHeight*devicePixelRatio;c.width=Math.floor(window.innerWidth*devicePixelRatio/fontSize)*fontSize;ctx.font=fontSize+"px arial";columns=c.width/fontSize;drops=[];for(var x=0;x<columns;x++)drops[x]=1}function draw(time){ctx.fillStyle="rgba(0,0,0, 0.05)";ctx.fillRect(0,0,c.width,c.height);ctx.fillStyle="#0F0";var inc=.04*Math.min(16.6,time-lastTime);for(var i=0;i<drops.length;i++){var text=characters[Math.floor(Math.random()*characters.length)];ctx.fillText(text,i*fontSize,drops[i]*fontSize);drops[i]+=inc;if(drops[i]*fontSize>c.height&&Math.random()>.975)drops[i]=0}lastTime=time;requestAnimationFrame(draw)}window.onresize=init;requestAnimationFrame(draw);